      border-radius: 48px;
      background: var(--primary-red);
      color: var(--primary-black);
      height: 48px;
      width: 48px;
      line-height: 48px;
      transition: all 0.25s 0s ease-in-out;
      overflow: hidden;
      margin: 1rem 0;
      padding-left: 0.75rem;
      box-sizing: border-box;
    }

    #mainNav ul a span {
      display: none;
      float: left;
      padding: 0 1rem;
    }

    #mainNav ul a i {
      font-size: 1.2rem;
      float: left;
      text-align: center;
      line-height: 48px;
    }

    #mainNav ul a:hover{
      width: 200px;
      background: var(--primary-black);
      color: var(--primary-white);
    }

      #mainNav ul a:hover span {
        display: block;
      }

/* Noticias portada */
.comicItem {
  margin-bottom: 2rem;
}

.comicItem h3 {
  background: var(--primary-black);
  padding: 1rem;
  font-size: 1.2rem;
  color: var(--primary-white);
  position: relative;
  top: 0;
  left: 0;
  margin: 0;
}

.comicItem a {
  display: block;
  color: var(--primary-black);
  background-color: var(--primary-red);
  width: 100px;
  text-align: center;
  padding: 0.5rem;
  text-decoration: none;
  font-weight: bold;
  transition: all 0.3s 0s ease-in-out;
}

.comicItem a span {
  display: none;
  float: left;
}

  .comicItem a:hover {
    width: 140px;
    background-color: var(--primary-black);
    color: var(--primary-white);
  }

  .comicItem a:hover span {
    display: block
  }

.listWrapper {
  background-color: var(--primary-black);
}

.characterItem {
  width: 20%;
  position: relative;
}

  .characterItem h3 {
    position: absolute;
    bottom: 0;
    width: 100%;
    background: var(--primary-red);
    margin: 0;
    padding: 1rem 0;
    text-align: center;
    color: var(--primary-white);
  }

.innerWeb #title_wrapper {
  margin-top: 8rem;
}

